import React, { useState, useEffect } from 'react';

const SearchCountries = () => {
  const [countries, setCountries] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredCountries, setFilteredCountries] = useState([]);

  // Simulate fetching country data
  useEffect(() => {
    const data = [
      { country: "United States", capital: "Washington, D.C." },
      { country: "Canada", capital: "Ottawa" },
      { country: "Brazil", capital: "Brasília" },
      { country: "United Kingdom", capital: "London" },
      { country: "Australia", capital: "Canberra" },
      { country: "Germany", capital: "Berlin" },
      { country: "France", capital: "Paris" },
      { country: "Italy", capital: "Rome" },
      { country: "Spain", capital: "Madrid" },
      { country: "Japan", capital: "Tokyo" },
      { country: "China", capital: "Beijing" },
      { country: "India", capital: "New Delhi" },
      { country: "South Africa", capital: "Pretoria" },
      { country: "Russia", capital: "Moscow" },
      { country: "Mexico", capital: "Mexico City" },
      { country: "Argentina", capital: "Buenos Aires" }
    ];

    setCountries(data);
  }, []);

  // Update search results as user types
  useEffect(() => {
    if (searchQuery.trim() !== '') {
      const searchResults = countries.filter(country =>
        country.country.toLowerCase().includes(searchQuery.toLowerCase()) ||
        country.capital.toLowerCase().includes(searchQuery.toLowerCase())
      );
      setFilteredCountries(searchResults);
    } else {
      setFilteredCountries([]); // Clear results if no search query
    }
  }, [searchQuery, countries]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value); // Update search query
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2 style={{ color: '#333' }}>Search Countries by Name or Capital</h2>
      <input
        type="text"
        value={searchQuery}
        onChange={handleSearchChange}
        placeholder="Search by country or capital"
        style={{
          padding: '10px',
          width: '300px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          marginBottom: '20px',
          fontSize: '16px',
        }}
      />
      {filteredCountries.length > 0 && (
        <ul style={{ listStyle: 'none', padding: 0 }}>
          {filteredCountries.map((country, index) => (
            <li key={index} style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>
              <strong>{country.country}</strong> - {country.capital}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchCountries;
