import React, { useState, useEffect } from 'react';

const SearchCountries = () => {
  const [countries, setCountries] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredCountries, setFilteredCountries] = useState([]);

  // Fetch country data from the provided link
  useEffect(() => {
    fetch('https://dpaste.com/79QXDY8TD/raw')  // Fetching the JSON data
      .then(response => response.json())
      .then(data => {
        setCountries(data);
        setFilteredCountries(data);  // Initially show all countries
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Update the search results as user types
  useEffect(() => {
    const searchResults = countries.filter(country =>
      country.country.toLowerCase().includes(searchQuery.toLowerCase()) ||
      country.city.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredCountries(searchResults);
  }, [searchQuery, countries]);

  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);  // Update search query
  };

  return (
    <div style={{ textAlign: 'center', padding: '20px' }}>
      <h2 style={{ color: '#333' }}>Search Countries and Capitals</h2>
      <input
        type="text"
        placeholder="Search by country or capital..."
        value={searchQuery}
        onChange={handleSearchChange}
        style={{
          padding: '10px',
          width: '300px',
          border: '1px solid #ccc',
          borderRadius: '5px',
          fontSize: '16px',
          marginBottom: '20px'
        }}
      />
      <div>
        {filteredCountries.length > 0 ? (
          <ul style={{ listStyleType: 'none', padding: '0' }}>
            {filteredCountries.map((country, index) => (
              <li key={index} style={{
                padding: '10px',
                marginBottom: '5px',
                border: '1px solid #ccc',
                borderRadius: '5px',
                backgroundColor: '#f9f9f9',
                display: 'inline-block',
                width: '300px',
                textAlign: 'left'
              }}>
                <strong>Country:</strong> {country.country} <br />
                <strong>Capital:</strong> {country.city}
              </li>
            ))}
          </ul>
        ) : (
          <p>No results found</p>
        )}
      </div>
    </div>
  );
};

export default SearchCountries;
